mapper.select:
SELECT user.id userId, user.nickName userName, acceptUser.nickName acceptUserName, nc.comment comment FROM normalcomment nc
LEFT JOIN user user ON  user.id = nc.user_id
LEFT JOIN user acceptUser ON acceptUser.id = nc.acceptuser_id
WHERE nc.type = #{type} AND nc.parentId = #{typeId}

  AND type_id=#{parentId}
