mapper.update:
UPDATE praisecollect


    praise = #{praise},


    collect = #{collect}


WHERE id = #{id}

mapper.select:
SELECT id, praise, collect, typeflag, type_id, parentId
FROM praisecollect
WHERE id = #{id}

SELECT id, praise, collect, typeflag, type_id, parentId
FROM praisecollect
WHERE typeflag = #{typeFlag} AND type_id = #{typeId} AND parentId = #{parentId}

SELECT id, praise, collect, typeflag, type_id, parentId
FROM praisecollect
WHERE type_id = #{typeId}

SELECT id, praise, collect, typeflag, type_id, parentId
FROM praisecollect
WHERE parentId = #{parentId}
